import React, { useState } from 'react';
import classes from './Dropdown.module.css'

 

class Dropdown extends React.Component {
	render(){

   const nav = {
		'React' : '/react',
		'Vue' : '/vue',
		'Angular' : '/angular'
	};

	return (
		<div className={classes.dropdown}>
			<div className={classes.dropdown_btn} >
			brandbooks
			<span className="fas_caret_down"></span></div>
			<nav>
				{nav.map (e => {
					return <li>{e}</li>
				})}
			</nav>
	 
		
			

		
		
			</div>
    )
   }
	}



 	


export default Dropdown;




