import React from 'react';
import classes from './Posts.module.css';
import Post from '../PublishPosts/Post';


const Posts = () => {

	let postsData = [
		{ id: 1, message: 'Hi, how are you?', likesCount: '20' },
		{ id: 2, message: 'It\'s my first post', likesCount: '15' }
	]

	let postsElements = posts.map(post => <Post id={post.id} message={post.message} likesCount={post.likesCount} />);




	return <div className={classes.myPost}>my post
		<form className={classes.form_post}>
			<p><textarea className={classes.textarea} placeholder="your news..."></textarea></p>
			<button className={classes.button}>send</button>
		</form>
		<div className={classes.wrapper_post}>
			{postsElements}
		</div>
	</div>
}

export default Posts
