import React from 'react';
import { BrandsSvgSelector } from './BrandsSvgSelector.tsx'
import  { useState } from 'react';
import classes from './ShowMore.module.css';



const ShowMore = ({ setSelected }) => {




	const Btn_ShowMore = () => {
		return (
			<div className={classes.button}>
				<p className={classes.txt}>Show more</p>
			  <BrandsSvgSelector className={classes.svg} id="arrow_icon" />
			</div>

		)
	}



	const [isActive, setIsActive] = useState(false);
	const options = Btn_ShowMore 


	return (
		<div className={classes.button}>

			<div className={classes.dropdown_btn} onClick={() =>
				setIsActive(!isActive)}>

				Show More<span className={classes.fas_caret_down}></span></div>
			{isActive && (
				<div className={classes.dropdown_content}>
					{options.map(option => (
						<div onClick={item => {
							setSelected(option)
							setIsActive(false)
						}}>

					  {option} 



						</div>
					))}
				</div>
			)}



		</div>

	)

}



export default ShowMore;
