import React from 'react';
import {brands} from  '../../OffersData' ;
import classes from './Brand.module.css';


const Brand = (props) => {



  const BrandsNav = brands.map((item) =>{
		return (
     <div  key={item.id} className={classes.brand_item}>
     <div>{item.label}</div>
	   </div>

		)
	})

	const [isActive, setIsActive] = useState(false);

	const options = BrandsNav 

	return (
		<div className={classes.dropdown}>
  
    <div className={classes.dropdown_btn} onClick={ (item) =>
		setIsActive(!isActive)}>
		
		Brandbooks<span className={classes.fas_caret_down}></span></div>
		{isActive && (
			<div className={classes.dropdown_content}>
				{options.map(option => (
					<div onClick ={item =>{
						setSelected(option)
						setIsActive (false)
					}}
					
					
					className={classes.dropdown_item}>

						<a>{option}</a>

						

					</div>
				))}
			</div>
		)}



		</div>

 	)




}

	

export default Brand ;
