import React from 'react';
import {brands} from  '../OffersData';
import classes from './Brands.module.css';
import  { useState } from 'react';
import { BrandsSvgSelector } from './BrandsSvgSelector.tsx'
// import 	ShowMore from './Btn_ShowMore/ShowMore.jsx';


const Brands = ({ setSelected }) => {

const BrandsNav = brands.map( (item) => 

<div className = {classes.brand} key = {item.id} id= {item.id}>
	
	  <div className={classes.wrapper}>
			<div className={classes.img}>
			<img src={item.logo} alt='logo' height={50} ></img>
			</div>
    <p className={classes.title}>{item.label}</p>
	
	</div>
</div>
)

const Btn_ShowMore = brands.map( (i) (

		<div className={classes.button}>
			 <a href='{item.googleplay}'>google</a>
			 <a href=''>google</a>
		</div>
	)
)


const [isActive, setIsActive] = useState(false);
const options = Btn_ShowMore


return (
	 <div className={classes.wrapper_brand} >
		 <div className={classes.button}>

<div className={classes.dropdown_btn} onClick={() =>
	setIsActive(!isActive)}>

	Show More<span className={classes.fas_caret_down}><div className={classes.svg}><BrandsSvgSelector  id="arrow_icon" /></div></span></div>
{isActive && (
	<div className={classes.ShowMore_content}>
		{options(option => (
			<div onClick={item => {
				setSelected(option)
				setIsActive(false)
			}}>
			{option} 
			</div>
		))}
	</div>
)}

    </div>
   {BrandsNav} 
	 </div>
)

}




export default Brands;









