import React from 'react';
import { NavLink } from 'react-router-dom';
import classes from './Dialogs.module.css';
import Dialog from './Dialog/Dialog';
import Message from './Message/Message';





const Dialogs = (props) => {


	 let dialogsData = [
	 	{ id: 1, name: 'Dimych' },
	 	{ id: 2, name: 'Yanych' },
	 	{ id: 3, name: 'Arthur' }
	 ]

 let dialogsElements = dialogsData.map(
 	dialog =>	<Dialog className={classes.dialog} name={dialog.name} id={dialog.id} /> );
	//массив диалогов(пользователей) на основе DATA (базы данных)



	 let messagesData = [
	 	{ id: 1, message: 'Hay-hay' },
	 	{ id: 2, message: 'Hay' },
	 	{ id: 3, message: 'hay' },
	 	{ id: 3, message: 'poka' }
	]


  let messagesElements = messagesData.map(
 	message => <Message id={message.id} message={message.message} />
  );
	//массив диалогов(сообщений) на основе DATA (базы данных)




	return (
		<div className={classes.dialogs}>
			<div className={classes.dialogsItems}>
				{dialogsElements}

			</div>

			<div className={classes.messages}>
				<div className={classes.message_wrapper}>
					{messagesElements}
				</div>
			</div>
		</div>
	)
}

export default Dialogs;
