	constructor(props){
		super(props);
		this.state = {
			show: 'show'
		}
	}


 showNav = () => {
      this.state.useState({show: 'hide'})
 }



	render(){
		let nav = this.props.nav



	return (
		<div className={classes.dropdown}>
			<div className={classes.dropdown_btn} onClick = {this.showNaw}>
			brandbooks
		  </div>

			<nav>
				{Object.keys(nav).map(elem => {
					return (
					 <ul>
						<li><a href = {nav[elem]}>{elem}</a></li>
						</ul>
						)
				})}
			</nav>
	 
		
			

		
		
			</div>
    )
   }
	}



 	


